import React from 'react'
import { useMutation } from '@apollo/client'
import { filter } from 'lodash'
import { GET_BOAT, REMOVE_BOAT } from '../../queries'
import { DeleteOutlined } from '@ant-design/icons'

const RemoveBoat = ({ id, firstName, lastName }) => {
  const [removeBoat] = useMutation(REMOVE_BOAT, {
    update(proxy, { data: { removeBoat } }) {
      const { people } = proxy.readQuery({ query: GET_BOAT })
      proxy.writeQuery({
        query: GET_BOAT,
        data: {
          people: filter(people, c => {
            return c.id !== removeBoat.id
          })
        }
      })
    }
  })
  const handleButtonClick = () => {
    let result = window.confirm('Are you sure you want to delete this person?')
    if (result) {
      removeBoat({
        variables: {
          id
        },
        optimisticResponse: {
          __typename: 'Mutation',
          removeBoat: {
            __typename: 'Person',
            id,
            firstName,
            lastName
          }
        }
      })
    }
  }
  return (
    <DeleteOutlined
      key='delete'
      onClick={handleButtonClick}
      style={{ color: 'red' }}
    />
  )
}

export default RemoveBoat
